import React,{useState, useEffect} from 'react';
import axios from 'axios';
import Pagination from './PaginationCharacter';
const CharacterPage = (props) =>{
    const [characters, setCharacters] = useState(undefined);
    const [loading, setLoading] = useState(true);
    const [offset , setOffset]= useState(0);
    const [currentPage, setCurrentPage]=useState(0);
    const [pageNumber, setPageNumber] = useState(0);
    const{perPage} = useState(20);
    const md5 = require('blueimp-md5');
    const publickey = 'b3f3ae85c881c6196c303b96f1678274';
    const privatekey = 'b82cf5047defc8b4ca20fd4bd5338432c0fe5739';
    const ts = new Date().getTime();
    const stringToHash = ts + privatekey + publickey;
    const hash = md5(stringToHash);
    ;
    useEffect(()=>{
        console.log("enter useEffect");
        setCurrentPage(currentPage+20);

        setPageNumber(pageNumber);
        async function fetchData() {
            try {
                console.log(offset);
                console.log(pageNumber);
                const {data} =await axios.get('https://gateway.marvel.com:443/v1/public/characters'+ '?ts=' + ts + '&apikey=' + publickey + '&hash=' + hash +'&limit=20&offset=' +offset);
                setCharacters(data);

                setLoading(false);
                console.log(data);
                setOffset(offset+20);
            }catch(e){
                console.log(e);
            }
        }
        fetchData();
    },[]);
    const previousClick = async (offsetValue) => {
        setOffset(offsetValue-20)
        const {data} =await axios.get('https://gateway.marvel.com:443/v1/public/characters'+ '?ts=' + ts + '&apikey=' + publickey + '&hash=' + hash +'&limit=20&offset=' +offsetValue);
                setCharacters(data);


      };
      const nextClick = async (offsetValue) => {
        setOffset(offsetValue+20)
        const {data} =await axios.get('https://gateway.marvel.com:443/v1/public/characters'+ '?ts=' + ts + '&apikey=' + publickey + '&hash=' + hash +'&limit=20&offset=' +offsetValue);
                setCharacters(data);


      };
    //const indexOfFirstPage = indexOfLastPage -currentPage;

    let characterData =(<div>
        {characters && characters.data.results.map((item, index)=>{
            return (
                <div className="pagecontainer">
                    <a className="pagecontent" href={`/characters/${item.id}`}>{item.name}</a>
                    </div>
            )
        })}
    </div>)

    if(loading){
        return(
            <div>
                <h2>Loading .....</h2>
            </div>
        )
    }
    else{
        return(
            <div className="container mt-5">
                <h1 className="text-primary mb-3">Character Names</h1>
                {characterData}
            <Pagination previousClick={previousClick} nextClick={nextClick} pageNumber={pageNumber} offset={offset}></Pagination>
            </div>
        )
    }
}

export default CharacterPage;
