import React,{useState, useEffect} from 'react';
import {useParams} from "react-router-dom";
import axios from 'axios';
import ReactPaginate from 'react-paginate';
import Paginator from "./Paginator";
const CharacterPage = (props) =>{
    const [characters, setCharacters] = useState({next: null, previous: null});
    const [loading, setLoading] = useState(true);
    let {page} = useParams();

    const md5 = require('blueimp-md5');
    const publickey = 'b3f3ae85c881c6196c303b96f1678274';
    const privatekey = 'b82cf5047defc8b4ca20fd4bd5338432c0fe5739';
    const ts = new Date().getTime();
    const stringToHash = ts + privatekey + publickey;
    const hash = md5(stringToHash);

    useEffect(()=>{
        console.log("enter useEffect");
        async function fetchData() {
            try {
               let url= "";
                if(page == 0){
                    url='https://gateway.marvel.com:443/v1/public/characters'+ '?ts=' + ts + '&apikey=' + publickey + '&hash=' + hash;
                }else{
                    url='https://gateway.marvel.com:443/v1/public/characters'+ '?ts=' + ts + '&apikey=' + publickey + '&hash=' + hash+'&limit=20'+  `&offset=${page *20}`;
                }
                const {data} =await axios.get(url);
                setCharacters(data);
                setLoading(false);

                console.log(characters);
            }catch(e){
                console.log(e);
            }
        }
        fetchData();
    },[page]);

    let characterdata =(<div>
        {characters && characters.data.results.map((character, index)=>{
            return (
                <div className="pagecontainer">
                    <a className="pagecontent" href={`/characters/${character.id}`}>{character.name}</a>
                    </div>
            )
        })}
    </div>)

    if(loading){
        return(
            <div>
                <h2>Loading .....</h2>
            </div>
        )
    }
    else{
        return(
            <div>
               <CharacterPage characters={characterdata}/>
               <Paginator first={0} last={parseInt(characterdata.count/20)} current={parseInt(page)} base="/characters/page"/>
            </div>
        )
    }
}

export default CharacterPage;
