import React,{useState, useEffect} from 'react';
import {useParams} from "react-router-dom";
import axios from 'axios';
const CharacterPage = (props) =>{
    const [characters, setCharacters] = useState(undefined);
    const [loading, setLoading] = useState(true);
    const [pageNumber, setPageNumber] = useState(0);
    const [paramValue, setParamValue] = useState(undefined);
    const md5 = require('blueimp-md5');
    const publickey = 'b3f3ae85c881c6196c303b96f1678274';
    const privatekey = 'b82cf5047defc8b4ca20fd4bd5338432c0fe5739';
    const ts = new Date().getTime();
    const stringToHash = ts + privatekey + publickey;
    const hash = md5(stringToHash);
    useEffect(()=>{
        const offset= pageNumber * 20;
        console.log("enter useEffect");
        setParamValue(parseInt(props.match.params.page));
        setPageNumber(parseInt(props.match.params.page));
        async function fetchData() {

            try {
                const {data} =await axios.get('https://gateway.marvel.com:443/v1/public/characters'+ '?ts=' + ts + '&apikey=' + publickey + '&hash=' + hash+'&limit=20'+ '&offset=' + offset);
                setCharacters(data);
                setLoading(false);
                console.log(data);
                console.log("props value " + paramValue)
                console.log("Offset is " + offset);
                console.log("page is " + pageNumber);
            }catch(e){
                console.log(e);
            }
        }
        fetchData();
    },[pageNumber, paramValue, props.match.params.page]);

    let characterdata =(<div>
        {characters && characters.data.results.map((character, index)=>{
            return (
                <div className="pagecontainer">
                    <a className="pagecontent" href={`/characters/${character.id}`}>{character.name}</a>
                    </div>
            )
        })}
    </div>)

    if(loading){
        return(
            <div>
                <h2>Loading .....</h2>
            </div>
        )
    }
    else{
        return(
            <div>
              <CharacterPage characters={characterdata}/>
              <div className="pagination">
                                {characters && characters.previous == null ? (<p></p>) : (<a href={`/characters/page/${paramValue - 1}`}><button className="btn">Previous</button> </a>)}

                                {characters && paramValue + 1 <= characters.data.count / 10 ? (
                                    <a href={`/characters/page/${paramValue + 1}`}>No. {paramValue + 1} </a>

                                ) : (<p></p>)}

                                {characters && paramValue + 2 <= characters.data.count / 10 ? (
                                    <a href={`/characters/page/${paramValue + 2}`}>No. {paramValue + 2} </a>

                                ) : (<p></p>)}

                                {characters && paramValue + 3 <= characters.data.count / 10 ? (
                                    <a href={`/characters/page/${paramValue + 3}`}>No. {paramValue + 3} </a>

                                ) : (<p></p>)}

                                {characters && paramValue + 4 <= characters.data.count / 10 ? (
                                    <a href={`/characters/page/${paramValue + 4}`}>No. {paramValue + 4} </a>

                                ) : (<p></p>)}

                                {characters && paramValue + 5 <= characters.data.count / 10 ? (
                                    <a href={`/characters/page/${paramValue + 5}`}>No. {paramValue + 5} </a>

                                ) : (<p></p>)}

                                {characters && characters.next == null ? (<p></p>) : (<a href={`/characters/page/${paramValue + 1}`}><button className="btn">Next</button></a>)}
                            </div>
            </div>
        )
    }
}

export default CharacterPage;
