import React,{useState, useEffect} from 'react';
import axios from 'axios';
import Pagination from './PaginationCharacter';
const CharacterPage = (props) =>{
    const [characters, setCharacters] = useState(undefined);
    const [loading, setLoading] = useState(true);
   const [offset, setOffset] = useState(0);
   const [paramValue, setParamValue] = useState(0);
    const md5 = require('blueimp-md5');
    const publickey = 'b3f3ae85c881c6196c303b96f1678274';
    const privatekey = 'b82cf5047defc8b4ca20fd4bd5338432c0fe5739';
    const ts = new Date().getTime();
    const stringToHash = ts + privatekey + publickey;
    const hash = md5(stringToHash);

    useEffect(()=>{
        console.log("enter useEffect");
        // setParamValue(parseInt(props.match.params.page));
        // setPageNumber(parseInt(props.match.params.page));
        async function fetchData() {
            try {
                console.log(offset);
                console.log(paramValue);
                //offsetValue=offset;
                const {data} =await axios.get('https://gateway.marvel.com:443/v1/public/characters'+ '?ts=' + ts + '&apikey=' + publickey + '&hash=' + hash +'&limit=20&offset=' +offset);
                setCharacters(data);

                setLoading(false);
                console.log(data);
            }catch(e){
                console.log(e);
            }
        }

        fetchData();
        setOffset(offset+20);
        console.log(offset);
        setParamValue(paramValue);

    },[]);

    //const indexOfFirstPage = indexOfLastPage -currentPage;

    let characterData =(<div>
        {characters && characters.data.results.map((item, index)=>{
            return (
                <div className="pagecontainer">
                    <a className="pagecontent" href={`/characters/${item.id}`}>{item.name}</a>
                    </div>
            )
        })}
    </div>)

    if(loading){
        return(
            <div>
                <h2>Loading .....</h2>
            </div>
        )
    }
    else{
        return(
            <div className="container mt-5">
                <h1 className="text-primary mb-3">Character Names</h1>
                {characterData}
            <div className="pagination">
                <a href={`/characters/page/${paramValue - 1}`}><button className="btn">Previous</button></a>
                {paramValue +1 < characters.count/20 ?(<a href={`/characters/page/${paramValue + 1}`}>No. {paramValue + 1})</a>) : (<p></p>)}
                {paramValue +2 < characters.count/20 ?(<a href={`/characters/page/${paramValue + 2}`}>No. {paramValue + 2})</a>) : (<p></p>)}
                {paramValue +3 < characters.count/20 ?(<a href={`/characters/page/${paramValue + 3}`}>No. {paramValue + 3})</a>) : (<p></p>)}
                {paramValue +4 < characters.count/20 ?(<a href={`/characters/page/${paramValue + 4}`}>No. {paramValue + 4})</a>) : (<p></p>)}
                {paramValue +5 < characters.count/20 ?(<a href={`/characters/page/${paramValue + 5}`}>No. {paramValue + 5})</a>) : (<p></p>)}
                <a href={`/characters/page/${paramValue + 1}`}><button className="btn">Next</button></a>
            </div>
            </div>
        )
    }
}

export default CharacterPage;
